import React, { useState } from 'react';
import axios from 'axios';

const LoginForm = () => {
    const [appID, setAppID] = useState('');
    const [appSecret, setAppSecret] = useState('');
    const [redirectURL, setRedirectURL] = useState('');
    const [result, setresult] = useState('');

    // const handleLogin = () => {
    //    //  Make a POST request to your Django backend to authenticate
    //     axios.post('http://localhost:8000/fyersLogin/loginfyers/', {
    //         app_id: appID,
    //         app_secret: appSecret,
    //         redirect_url: redirectURL
    //     })
        
    //     .then(response => {
    //         // Handle successful authentication
    //         console.log('Login successful:', response.data);
    //     })
    //     .catch(error => {
    //         // Handle authentication error
    //         console.error('Login failed:', error);
    //     });

        
    // };
    
    const handleLogin = async () => {
        





        try {
            // axios.get('http://localhost:8000/api/hello-world/')

            const response = await axios.post('http://localhost:8000/fyersLogin/loginfyers/', {
                params: {  app_id: appID,
                            app_secret: appSecret,
                            redirect_url: redirectURL }
            });
            setresult(response.data);
        } catch (error) {

            console.error('Error fetching trading script:', error);
        }finally{
            console.log(result)
        }

    };

    return (
        <div>
            <input type="text" placeholder="App ID" value={appID} onChange={e => setAppID(e.target.value)} />
            <input type="password" placeholder="App Secret" value={appSecret} onChange={e => setAppSecret(e.target.value)} />
            <input type="text" placeholder="Redirect URL" value={redirectURL} onChange={e => setRedirectURL(e.target.value)} />
            <button onClick={handleLogin}>Login</button>
        </div>
    );
};

export default LoginForm;
