import React, { useState } from 'react';
import axios from 'axios';

const LoginForm = () => {
  const [formData, setFormData] = useState({
    app_id: '',
    app_secret: '',
    redirect_url: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const params = {
        app_id: formData.app_id,
        app_secret: formData.app_secret,
        redirect_url: formData.redirect_url
      };
    try {
    //   const response = await axios.post('http://localhost:8000/fyersLogin/loginfyers/',   {params: { script_name: 'scriptName', date: 'uday' }});
      const response = axios.post('http://localhost:8000/fyersLogin/loginfyers/', null, {
        params: params
      })
      console.log(response);
      // Handle response data as needed
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="app_id" value={formData.app_id} onChange={handleChange} placeholder="App ID" />
      <input type="text" name="app_secret" value={formData.app_secret} onChange={handleChange} placeholder="App Secret" />
      <input type="text" name="redirect_url" value={formData.redirect_url} onChange={handleChange} placeholder="Redirect URL" />
      <button type="submit">Submit</button>
      <h1>The value :{}</h1>
    </form>
  );
};

export default LoginForm;
