import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';

const LoginForm = () => {
  const [formData, setFormData] = useState({
    app_id: '',
    app_secret: '',
    redirect_url: ''
  });

  // const [resu, setresult] = useState()
  // const handleChange = (e) => {
  //   const { name, value } = e.target;
  //   setFormData({ ...formData, [name]: value });

  // };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const params = {
      app_code: formData.app_id,
      app_secret: formData.app_secret,
      redirect_url: formData.redirect_url
    };
    try {
      const response = await axios.post('http://localhost:8000/fyersLogin/loginfyers/', { params: params });
      //   const response = axios.post('http://localhost:8000/fyersLogin/fyersauth/', null, {
      //     params: params
      //   })
      console.log(response)
      // setresult(response)
      // Handle response data as needed
    } catch (error) {
      console.error('Error submitting form:', error);
    }
    finally {
      // console.log(response)
    }


  };


  const [authCode, setAuthCode] = useState('');
  const params1 = {
    app_id: '1',
    auth: "formData.app_secret"
  };
  const handleChange1 = (event) => {
    setAuthCode(event.target.value);
    // const params1 = {
    //     app_id: '1',
    //     auth: "formData.app_secret"
    //   };
  };

  const handleSubmit1 = async (event) => {
    const params = {
      app_code: authCode,

    };
    event.preventDefault();
    try {
      // Make a POST request to submit the authentication code to Django
      const response = axios.post('http://localhost:8000/fyersLogin/fyersauth/', null, {
        params: params
      })
      console.log('Authentication code submitted successfully:');
      // Handle success (e.g., display a success message)
    } catch (error) {
      console.error('Error submitting authentication code:', error);
      // Handle error (e.g., display an error message)
    }
    finally{
      // console.log(response)
    }
  };



  return (<>

    <div className="login-form-page">
      
      <div className="login-form-container">
        <form className="login-form" onSubmit={handleSubmit}>
          <div className='login_heading'>Auth login</div>
          <input className="login-field login-input" type="text" name="app_id" value={formData.app_id} onChange={handleChange} placeholder="App ID" />
          <input className="login-field login-input" type="text" name="app_secret" value={formData.app_secret} onChange={handleChange} placeholder="App Secret" />
          <input className="login-field login-input" type="text" name="redirect_url" value={formData.redirect_url} onChange={handleChange} placeholder="Redirect URL" />
          <button className="login-field login-button" type="submit">Submit</button>
          <p></p>
        </form>

        <div>
        <span>-</span>
        <span>-</span>
        <span>-</span>
        </div>

        <form className="login--container" onSubmit={handleSubmit1}>
      <label>
        Auth Code:
        <input className="login-input login-field" 
          type="text"
          value={authCode}
          onChange={handleChange1}
          placeholder="Enter authentication code"
        />
      </label>
      
      <button className="login-button login-field"  type="submit">Submit</button>
    </form>

      </div>
      

    </div>




  </>);
};

export default LoginForm;
