import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';

const LoginForm = () => {
  const [formData, setFormData] = useState({
    app_id: '',
    app_secret: '',
    redirect_url: ''
  });

  const [resu, setresult] = useState()
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const params = {
      app_code: formData.app_id,
      app_secret: formData.app_secret,
      redirect_url: formData.redirect_url
    };
    try {
      const response = await axios.post('http://localhost:8000/fyersLogin/loginfyers/', { params: params });
      //   const response = axios.post('http://localhost:8000/fyersLogin/fyersauth/', null, {
      //     params: params
      //   })
      console.log(response)
      setresult(response)
      // Handle response data as needed
    } catch (error) {
      console.error('Error submitting form:', error);
    }
    finally {
      // console.log(response)
    }


  };


  const [authCode, setAuthCode] = useState('');
  const params1 = {
    app_id: '1',
    auth: "formData.app_secret"
  };
  const handleChange1 = (event) => {
    setAuthCode(event.target.value);
    // const params1 = {
    //     app_id: '1',
    //     auth: "formData.app_secret"
    //   };
  };

  const handleSubmit1 = async (event) => {
    const params = {
      app_code: authCode,

    };
    event.preventDefault();
    try {
      // Make a POST request to submit the authentication code to Django
      const response = axios.post('http://localhost:8000/fyersLogin/fyersauth/', null, {
        params: params
      })
      console.log('Authentication code submitted successfully:');
      // Handle success (e.g., display a success message)
    } catch (error) {
      console.error('Error submitting authentication code:', error);
      // Handle error (e.g., display an error message)
    }
  };





  return (<>
    <form onSubmit={handleSubmit}>
    <label> Auth Code:<input type="text" name="app_id" value={formData.app_id} onChange={handleChange} placeholder="App ID" /></label>
      <input type="text" name="app_secret" value={formData.app_secret} onChange={handleChange} placeholder="App Secret" />
      <input type="text" name="redirect_url" value={formData.redirect_url} onChange={handleChange} placeholder="Redirect URL" />
      <button type="submit">Submit</button>
      <p></p>
    </form>


    <form onSubmit={handleSubmit1}>
      <label>
        Auth Code:
        <input
          type="text"
          value={authCode}
          onChange={handleChange1}
          placeholder="Enter authentication code"
        />
      </label>
      <button type="submit">Submit</button>
    </form>

  </>);
};

export default LoginForm;
